import React, { useState } from 'react';
import arrow from '../img/arrow.png';
import { useNavigate } from 'react-router-dom';
import '../css/signup.css';

const Signup = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [usernameError, setUsernameError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [confirmPasswordError, setConfirmPasswordError] = useState('');
  const [isSignupModalOpen, setIsSignupModalOpen] = useState(false); // State for signup success modal
  const [isOtpModalOpen, setIsOtpModalOpen] = useState(false); // State for OTP modal
  const [otp, setOtp] = useState(''); // OTP input state
  const navigate = useNavigate();

  const validatePassword = (password) => {
    const lengthCheck = password.length >= 8;
    const lowercaseCheck = /[a-z]/.test(password);
    const uppercaseCheck = /[A-Z]/.test(password);
    const numberCheck = /[0-9]/.test(password);
    const specialCharCheck = /[!@#$%^&*(),.?":{}|<>]/.test(password);
    return lengthCheck && lowercaseCheck && uppercaseCheck && numberCheck && specialCharCheck;
  };

  const handleSignup = async (event) => {
    event.preventDefault();
    setUsernameError('');
    setPasswordError('');
    setConfirmPasswordError('');

    if (password !== confirmPassword) {
      setConfirmPasswordError("Passwords do not match!");
      return;
    }

    if (!validatePassword(password)) {
      setPasswordError(
        "Password must be at least 8 characters long and include a mix of lowercase and uppercase letters, numbers, and special symbols."
      );
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, email, password })
      });

      const data = await response.json();

      if (response.ok) {
        setIsSignupModalOpen(true);

        setTimeout(() => {
          setIsSignupModalOpen(false);
          setIsOtpModalOpen(true); // Open OTP modal after signup success
        }, 3000); // Show signup success modal for 3 seconds
      } else if (data.message === 'Username is taken') {
        setUsernameError('Username is already taken.');
      } else {
        setPasswordError(`Signup failed: ${data.message}`);
      }
    } catch (error) {
      console.error('Error:', error);
      setPasswordError('Signup failed. Please try again later.');
    }
  };

  const handleOtpVerification = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, otp })
      });

      const data = await response.json();

      if (response.ok) {
        setIsOtpModalOpen(false); // Close OTP modal
        navigate('/login'); // Redirect to login page on successful OTP verification
      } else {
        alert(`OTP verification failed: ${data.message}`);
      }
    } catch (error) {
      console.error('Error:', error);
      alert('OTP verification failed. Please try again later.');
    }
  };

  return (
    <div className="background_signup">

      <div className="signup">
        <header className="header" id="header">
          <div className="logo_img"></div>
        </header>

        <main className="main">
          <section className="signup section" id="signup">
            <div className="signup_container">
              <form className="signup_form" onSubmit={handleSignup}>
                <h2 className="signup_title">SIGN UP</h2>
                <input
                  type="text"
                  className='user_txtbox'
                  placeholder="USERNAME"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
                <input
                  type="email"
                  className='email_txtbox'
                  placeholder="EMAIL"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
                <input
                  type="password"
                  className='pass_txtbox'
                  placeholder="PASSWORD"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
                <input
                  type="password"
                  className='confirmpass_txtbox'
                  placeholder="CONFIRM PASSWORD"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                />
                {usernameError && <p className="error_text">{usernameError}</p>}
                {passwordError && <p className="error_text">{passwordError}</p>}
                {confirmPasswordError && <p className="error_text">{confirmPasswordError}</p>}

                <button type="submit" className="signupbtn">
                  <img src={arrow} alt="Arrow" />
                </button>
                <p className="login_btn"><a href="/login">ALREADY HAVE AN ACCOUNT?</a></p>
              </form>
            </div>
          </section>
        </main>

        {/* Signup success modal */}
        {isSignupModalOpen && (
          <div className="modal">
            <div className="modal_content">
              <h3 className='modal_title'>Signup Successful!</h3>
              <p className='modal_phrase'>Welcome, <strong>{username}!</strong> Redirecting to OTP verification...</p>
            </div>
          </div>
        )}

        {/* OTP verification modal */}
        {isOtpModalOpen && (
          <div className="modal">
            <div className="modal_content">
              <h3 className='modal_title'>OTP Verification</h3>
              <p className='modal_phrase'>Enter the OTP sent to <strong>{email}</strong>:</p>
              <input
                type="text"
                value={otp}
                onChange={(e) => setOtp(e.target.value)}
                placeholder="Enter OTP"
                required
              />
              <button onClick={handleOtpVerification}>Verify OTP</button>
            </div>
          </div>
        )}

      </div>
    </div>
  );
};

export default Signup;
