import React from 'react';
import klimalogo from '../img/klima logo.png';
import klimatxt from '../img/klima text.png';
import rerend from '../img/rerend logo.png';
import gp from '../img/gp logo.png';
import download from '../img/download btn.png';
import '../css/homepage.css'; 

const Homepage = () => {
  return (
    <div className="background_homepage">
      {/* Flex container for centering title image and subtitle */}
      <div className="centered_container">
        <img src={klimatxt} alt="KLIMA Logo" className="title_img" />
        <h1 className='subtitle'>Act Now for Nature â€“ A Future Worth Protecting</h1>

        {/* login */}
        <button className="image_button">
          <img src={download} alt="Click Me" className="button_img" />
        </button>
        
      </div>

      {/* Game Information Section */}
      <div className="main_container" >
        <div className="game_info">
          <h2 className="game_title">About the Game</h2>
          <p className="game_description">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Quisque
            faucibus, nunc at egestas euismod, nisl nisl bibendum risus, a
            vestibulum nulla sapien et urna. Sed ac felis sed felis ullamcorper
            tincidunt. Integer vitae ligula consequat, dapibus lectus sed,
            malesuada justo. Ut euismod, mi vel pretium blandit, ante urna
            volutpat arcu, id ultricies leo ante non magna. Morbi et cursus
            ligula, et dapibus libero. Aliquam erat volutpat.
          </p>
          <br></br>
          <p className="game_description">
            Phasellus sed felis tincidunt, auctor arcu in, dictum nunc. Aenean
            ultricies vitae orci in lacinia. Sed vitae arcu at quam cursus
            tincidunt. Praesent sit amet leo libero. Integer id mauris euismod,
            dapibus neque nec, condimentum lacus. Duis sit amet lorem sit amet
            eros varius dignissim.
          </p>
        </div>
      

        <footer className="footer">
          <div className="footer_container">
            <p className="footer_copy">&#169; KLIMA 2024 | All rights reserved.</p>
            <img src={gp} alt="Bottom Left" className="bottom_left" />
            <img src={rerend} alt="Bottom Left" className="bottom_mid" />
            <img src={klimalogo} alt="Bottom Right" className="bottom_right" />
          </div>
        </footer>
      </div>
    </div>
  );
};

export default Homepage;
